     'type'   =>   'A'
 ]);
Export  Export DNS record as   BIND config .     $response   =   $client  ->  zones  ()  ->  dns  ()  ->  export  (  'zone_id'  );

 file_put_contents  (  './test.txt'  , $response  ->  body  ());
